        public JsonResult GetDistributionData(FilterData filter)
        {

            string sqParam = $"'{filter.FromDate}','{filter.BenType}','{filter.BenStatus}','{filter.InCare}','{filter.CenterId}','{filter.DistrictCode}','{filter.UpazilaCode}','{filter.UnionCode}','{filter.VillageCode}','{filter.Users}'";
            try
            {
                DistributionReport result = new DistributionReport();
                string sql = "EXEC Report_Distribution_Top " + sqParam;
                result.Distribution_Report_Top = unitOfWork.GenericRepositories<Distribution_Report_Top>().GetRecordSet(sql).FirstOrDefault();
                sql = "EXEC Report_Distribution_BEN " + sqParam;
                result.Distribution_BenInfo = unitOfWork.GenericRepositories<Distribution_BenInfo>().GetRecordSet(sql).ToList();
                var JsonData = Json(new { success = true, data = result }, JsonRequestBehavior.AllowGet);
                JsonData.MaxJsonLength = int.MaxValue;
                return JsonData;
            }
            catch (Exception ex)
            {
                return Json(new { success = true, Data = ex.Message }, JsonRequestBehavior.AllowGet);
            }
        }
        public ActionResult ExportReport()
        {
            try
            {
                FilterData filter = GetFromQueryString<FilterData>();

                string sqParam = $"'{filter.FromDate}','{filter.BenType}','{filter.BenStatus}','{filter.InCare}','{filter.CenterId}','{filter.DistrictCode}','{filter.UpazilaCode}','{filter.UnionCode}','{filter.VillageCode}','{filter.Users}'";
                ReportDocument rpt = new ReportDocument();
                DataSet dSt = new DataSet();
                string sql = "EXEC Report_Distribution_Top " + sqParam;
                var dtTop = unitOfWork.GetDataTable(sql);
                sql = "EXEC Report_Distribution_BEN " + sqParam;
                var dtBen = unitOfWork.GetDataTable(sql);

                for (int i = 0; i < dtBen.Rows.Count; i++)
                {
                    if (dtBen.Rows[i]["Signature"].ToString() != "" || dtBen.Rows[i]["Signature"].ToString() != null)
                        dtBen.Rows[i]["benfingerImage"] = Convert.FromBase64String(dtBen.Rows[i]["Signature"].ToString());
                    //else
                    //dtBen.Rows[i]["benfingerImage"] = Convert.FromBase64String("");

                }
                dSt.Tables.Add(dtTop);
                dSt.Tables.Add(dtBen);

                dSt.Tables[0].TableName = "Report_Distribution_Top";
                dSt.Tables[1].TableName = "Report_Distribution_BEN";

                rpt.Load(System.Web.Hosting.HostingEnvironment.MapPath("~\\Report\\Distribution_Report.rpt"));
                rpt.SetDataSource(dSt);
                rpt.ExportToHttpResponse(ExportFormatType.PortableDocFormat, System.Web.HttpContext.Current.Response, false, "Distribution Report");
                return Json(new { success = true, Data = "Exported Successfully!" }, JsonRequestBehavior.AllowGet);
            }
            catch (Exception ex)
            {
                return Json(new { success = false, Data = ex.Message }, JsonRequestBehavior.AllowGet);
            }
        }

        public T GetFromQueryString<T>() where T : new()
        {
            var obj = new T();
            var properties = typeof(T).GetProperties(); // to get all properties from Class(Object)  
            foreach (var property in properties)
            {
                var valueAsString = Request.QueryString[property.Name];
                object value = Parse(property.PropertyType, valueAsString); // parse data types  

                if (value == null)
                    continue;

                property.SetValue(obj, value, null); //set values to properties.  
            }
            return obj;
        }

        public object Parse(Type dataType, string ValueToConvert)
        {
            TypeConverter obj = TypeDescriptor.GetConverter(dataType);
            object value = obj.ConvertFromString(null, CultureInfo.InvariantCulture, ValueToConvert);
            return value;
        }
    }
    
